import React from "react";
import LoginBox from "./page/auth/login";
import RegisterBox from "./page/auth/register";


class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      isLoginOpen: true,
      isRegisterOpen: false,
      };
    }

    showLoginBox(){
      this.setState({isLoginOpen: true, isRegisterOpen: false});
    }
    showRegisterBox(){
      this.setState({isLoginOpen: false, isRegisterOpen: true});
    }

    render(){
      return (
          <div className = "rootContainer">
            <div className = "boxContainer">
              {this.state.isLoginOpen && <LoginBox/>}
              {this.state.isRegisterOpen && <RegisterBox/>}
              {this.state.isLoginOpen && <div className = "controller" onClick={this.showRegisterBox.bind(this)}>
                    회원가입
                </div>}
                {this.state.isRegisterOpen && <div className = "controller" onClick={this.showLoginBox.bind(this)}>
                    로그인
                </div>}
            </div>

          </div>
      );
    }
  }
  export default App;
